    var Model = Backbone.Model.extend({
      parse: function(data) { return data.wrapper; }
    });

    var Col = Backbone.Collection.extend({model: Model});
    var col = new Col;

    col.set([{wrapper: {id: 1, name: 'Foo'}}], {parse: true, merge: true});
