```
trigger : function(ev) {
  var list, calls, i, l;
  if (!(calls = this._callbacks)) return this;
  if (list = calls[ev]) {
    for (i = 0, l = list.length; i < l; i++) {
      // if the callback unbinds itself then the list length changes and the
      // following events will not be called in the correct order or at all.
      list[i].apply(this, Array.prototype.slice.call(arguments, 1));
